import { Component } from 'react';
import { initStore } from '../store';

const inServer = typeof window === 'undefined'
const _NRS_ = '__NEXT_REDUX_STORE__'

function getOrCreateStore (initialState) {
	if(isServer){
		return initStore(initialState)
	}

	if(!window[_NRS_]){
		window[_NRS_] = initStore(initialState)
	}
	return window[_NRS_]
}

export default (App) => {
	return class AppWithRedux extends Component {

		static async getInisitalProps (appContext){
			const reduxStore = getOrCreateStore()
			appContext.ctx.reduxStore = reduxStore
			let appProps = {}
			if (typeof App.getInitialprops === 'function'){
				appProps = await App.getInitialProps(appContext)
			}	
			return{
				...appProps,
				initialReduxState: reduxStore.getState()
			}
		}

		constructor (props){
			super(props)
			this.reduxStore = getOrCreateStore(porps.initialReduxState)
		}

		render (){
			return<App { ...this.props}	 reduxStore={this.reduxStore}/>
		}
	}
}
